import React, { useEffect, useState } from 'react';
import './App.css';
import getCompanies from './api/getCompanies';
import { CompanyType } from './types';

function App() {
  const [companies, setCompanies] = useState<CompanyType[]>([])
  const [currentPage, setCurrentPage] = useState<number>(1)
  const [companiesToDisplay, setCompaniesToDisplay] = useState<CompanyType[]>([])
  let pageCounts = Math.ceil(companies.length / 10)

  const fetchCompanies = async () => {
    const comp: any = await getCompanies()
    comp.length && setCompanies(comp)
  }

  const handlePageChange = (nextPage: number) => {
    setCurrentPage(nextPage)
  }

  useEffect(() => {
    fetchCompanies()
  }, [])

  var arrays: any = [];
  const chunkSize = 10;


  ;
  for (let i = 0; i < companies.length; i += chunkSize) {
    const chunk = companies.slice(i, i + chunkSize);
    arrays.push(chunk)
}

  useEffect(() => {
    let comp = arrays[currentPage - 1]

    setCompaniesToDisplay(comp)
  }, [currentPage, arrays.length])


  return (
    <div>
      <header>

      </header>
      <section>
        <table>
          <thead>
          <tr>
            <th>id</th>
            <th>Name</th>
            <th>Description</th>
            <th>Website</th>
          </tr>
          </thead> 
          <tbody>
           {companiesToDisplay?.map(c => {
            return <tr key={c.name} style={{ }}>
            <td className='cell'>{c.id}</td>
            <td className='cell'>{c.name}</td>
            <td className='cell'>{c.description}</td>
            <td className='cell'>{c.website}</td>
          </tr>
           })} 
          </tbody>
        </table>
        <div className='paginationContainer'>
          <button disabled={currentPage === 1} onClick={() => handlePageChange(currentPage - 1)}>Prev</button>
          <button onClick={() => handlePageChange(1)}>1</button>
          <button onClick={() => handlePageChange(2)}>2</button>
          <button onClick={() => handlePageChange(3)}>3</button>
          <span>...</span>
          <button disabled={currentPage === pageCounts} onClick={() => handlePageChange(currentPage + 1)}>Next</button>
        </div>
        {/* <ul>
          {companies.slice(0,10).map(c => {
            return <div key={c.id}>
            <p>{c.name}</p>
            </div>
          })}
        </ul> */}

      </section>
    </div>
  );
}

export default App;
