import { useEffect } from 'react';
import './App.css';
import getCompanies from './api/getCompanies';
import { StateType } from './types';
import setCompanies from './actions/getCompanies';
import setCompaniesToDisplay from './actions/setCompaniesToDisplay';
import setCurrentPage from './actions/setCurrentPage';
import { useDispatch, useSelector } from 'react-redux';

function App() {
  const dispatch = useDispatch();
  const companies = useSelector((state: StateType) => {
    console.log('state::::::', state);
    return state.companies
  })
  const companiesToDisplay = useSelector((state: StateType) => state.companiesToDisplay)
  const currentPage = useSelector((state: StateType) => state.currentPage)
  let pageCounts = Math.ceil(companies?.length / 10)

  const fetchCompanies = async () => {
    const comp: any = await getCompanies()
    console.log('comp::::::', comp);
    dispatch(setCompanies(comp))
  }

  const handlePageChange = (nextPage: number) => {
    dispatch(setCurrentPage(nextPage))
  }

  useEffect(() => {
    fetchCompanies()
  }, [])

  var arrays: any = [];
  const chunkSize = 10;

console.log('companies::::::', companies);
  ;
  for (let i = 0; i < companies?.length; i += chunkSize) {
    const chunk = companies.slice(i, i + chunkSize);
    arrays.push(chunk)
}

  useEffect(() => {
    let comp = arrays[currentPage - 1]

    dispatch(setCompaniesToDisplay(comp))
  }, [currentPage, arrays?.length])


  return (
    <div>
      <header>

      </header>
      <section>
        <table>
          <thead>
          <tr>
            <th>id</th>
            <th>Name</th>
            <th>Description</th>
            <th>Website</th>
          </tr>
          </thead> 
          <tbody>
           {companiesToDisplay?.map(c => {
            return <tr key={c.name} style={{ }}>
            <td className='cell'>{c.id}</td>
            <td className='cell'>{c.name}</td>
            <td className='cell'>{c.description}</td>
            <td className='cell'>{c.website}</td>
          </tr>
           })} 
          </tbody>
        </table>
        <div className='paginationContainer'>
          <button disabled={currentPage === 1} onClick={() => handlePageChange(currentPage - 1)}>Prev</button>
          <button onClick={() => handlePageChange(1)}>1</button>
          <button onClick={() => handlePageChange(2)}>2</button>
          <button onClick={() => handlePageChange(3)}>3</button>
          <span>...</span>
          <button disabled={currentPage === pageCounts} onClick={() => handlePageChange(currentPage + 1)}>Next</button>
        </div>
      </section>
    </div>
  );
}

export default App;
