import React, { useEffect, useState } from 'react';
import logo from './logo.svg';
import './App.css';
import getCompanies from './api/getCompanies';
import { CompanyType } from './types';

function App() {
  const [companies, setCompanies] = useState<CompanyType[]>([])
  const fetchCompanies = async() => {
    const comp: any = await getCompanies()
    setCompanies(comp)
  }
  useEffect(() => {
    fetchCompanies()
  }, [])

  return (
    <div>
      <header>

      </header>
      <section>
        <table>
          <tr>
            <th>id</th>
            <th>Name</th>
            <th>Description</th>
            <th>Website</th>
          </tr>
          {companies.slice(0,10).map(c => {
            return <tr key={c.id}>
            <td>{c.id}</td>
            <td>{c.name}</td>
            <td>{c.description}</td>
            <td>{c.website}</td>
            </tr>
          })}
        </table>
        {/* <ul>
          {companies.slice(0,10).map(c => {
            return <div key={c.id}>
            <p>{c.name}</p>
            </div>
          })}
        </ul> */}
      </section>
    </div>
  );
}

export default App;
